mapper.select:
select setmeal.* , category.name from setmeal, category

    setmeal.category_id = category.id

        and setmeal.name like concat('%', #{name}, '%')


        and setmeal.status = #{status}


        and category_id = #{categoryId}


order by setmeal.create_time desc

select * from setmeal

     and name like concat('%',#{name},'%') 
     and category_id = #{categoryId} 
     and status = #{status}

select sd.name , sd.copies, d.description , d.image from setmeal_dish as sd
left join dish as d on sd.dish_id = d.id

    sd.setmeal_id = #{setmealId}

select count(id) from setmeal

     and status = #{status} 
     and category_id = #{categoryId}
